INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:05:21', '49319', '木贼科  Equisetaceae', '西藏植物志第1卷蕨类植物门 Pteridophyta木贼科 Equisetaceae', ' 4. 木贼科 Equisetaceae
