INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:05:41', '49368', '笔管草  Hippochaete debilis (Roxb.) Ching', '西藏植物志第1卷木贼科 Equisetaceae木贼属 Hippochaete Milde笔管草 Hippochaete debilis (Roxb.) Ching', '2. 笔管草 图8: 4-5
