INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:05:44', '49374', '小阴地蕨属  Botrychium Sw.', '西藏植物志第1卷阴地蕨科 Botrychiaceae小阴地蕨属 Botrychium Sw.', ' 1. 小阴地蕨属 Botrychium Sw.
