INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:05:59', '49412', '蕗蕨属  Mecodium Presl.', '西藏植物志第1卷膜蕨科 Hymenophyllaceae蕗蕨属 Mecodium Presl.', '  2. 蕗蕨属 Mecodium Presl.
