INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:06:01', '49415', '蕗蕨  Mecodium badium (Hook. et Grev.) Copel.', '西藏植物志第1卷膜蕨科 Hymenophyllaceae蕗蕨属 Mecodium Presl.蕗蕨 Mecodium badium (Hook. et Grev.) Copel.', ' 3. 蕗蕨 图13：1-3
    Mecodium badium (Hook. et Grev.) Copel. 
