INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:06:18', '49456', '蕨科  Pteridiaceae', '西藏植物志第1卷蕨类植物门 Pteridophyta蕨科 Pteridiaceae', '   17. 蕨科 Pteridiaceae
