INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:06:20', '49461', '凤尾蕨属  Pteris L.', '西藏植物志第1卷凤尾蕨科 Pteridaceae凤尾蕨属 Pteris L.', '    1. 凤尾蕨属 Pteris L.
