INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:06:23', '49468', '粗糙凤尾蕨  Pteris cretica L. var. laeta Tard. -Blot et C. Chr.', '西藏植物志第1卷凤尾蕨科 Pteridaceae凤尾蕨属 Pteris L.粗糙凤尾蕨 Pteris cretica L. var. laeta Tard. -Blot et C. Chr.', ' 7. 粗糙凤尾蕨
    Pteris cretica L. var. laeta Tard. -Blot et C. Chr. 
