INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:06:31', '49487', '黑足金粉蕨  Onychium contiguum (Wall.) Hope', '西藏植物志第1卷中国蕨科 Sinopteridaceae金粉蕨属 Onychium Kaulfuss黑足金粉蕨 Onychium contiguum (Wall.) Hope', ' 3. 黑足金粉蕨
