INSERT INTO wcc.dcp (t, dcpid, category, patbook, content, indexTb) VALUES ('2018-12-13 01:06:35', '49496', '金粉背蕨  Aleuritopteris chrysophylla (Hook.) Ching', '西藏植物志第1卷中国蕨科 Sinopteridaceae粉背蕨属 Aleuritopteris Fee金粉背蕨 Aleuritopteris chrysophylla (Hook.) Ching', '7. 金粉背蕨
